insert into CauHoi(NoiDung,CauA,CauB,CauC,CauD,CauTLDung) values(N'Băng vòng xoắn:', N'A. Là đưa cuộn băng đi nhiều vòng từ trên xuống dưới theo hình vòng xoắn lò xo hoặc như hình con rắn quấn quanh thân cây, vòng sau đè lên 2/3 vòng trước.', N'B. Là đưa cuộn băng đi nhiều vòng từ dưới lên trên, từ trong ra ngoài, đường băng sau đè lên 1/3 đường băng trước theo hình vòng xoắn lò xo.', N'C. Là đưa cuộn băng đi nhiều vòng từ dưới lên trên theo hình vòng xoắn lò xo hoặc như hình con rắn quấn quanh thân cây, vòng sau đè lên 2/3 vòng trước.', N'D. Là đưa cuộn băng nhiều vòng theo hình số 8 hoặc 2 vòng đối xứng, băng từ dưới lên trên, từ ngoài vào trong, đường băng sau đè lên 2/3 đường băng trước.', 'c');
insert into CauHoi(NoiDung,CauA,CauB,CauC,CauD,CauTLDung) values(N'Băng số 8:', N'A. Là đưa cuộn băng đi nhiều vòng từ dưới lên trên theo hình vòng xoắn lò xo, đường băng sau đè lên 2/3 đường băng trước.', N'B. Là đưa cuộn băng đi vòng theo hình số 8, băng từ trên xuống dưới hoặc từ ngoài vào trong, đường băng sau đè lên 2/3 đường băng trước.', N'C. Là đưa cuộn băng đi vòng theo hình số 8, băng từ trên xuống dưới hoặc từ ngoài vào trong, đường băng sau đè lên 1/3 đường băng trước.', N'D. Là đưa cuộn băng đi vòng theo hình số 8, băng từ dưới lên trên hoặc từ ngoài vào trong, đường băng sau đè lên 2/3 đường băng trước.', 'd');
insert into CauHoi(NoiDung,CauA,CauB,CauC,CauD,CauTLDung) values(N'Mang thương bình bằng tay, áp dụng trong chiến đấu để:', N'A. Vận chuyển thương binh ở những khoảng cách ngắn như: bế, cõng, bò chuyển thương binh.', N'B. Vận chuyển thương binh ở địa hình rừng núi, hai tay người tải thương được tự do bám, nắm, leo, trèo.', N'C. Vận chuyển thương binh khi có những vết thương nặng trên người, không thể nằm được trên cáng hoặc võng.', N'D. Vận chuyển thương binh ở những khoảng cách xa, dùng cáng hoặc võng phải hai người khiêng; bế, cõng, bị chuyển thương chỉ cần 1 người.', 'a');
insert into CauHoi(NoiDung,CauA,CauB,CauC,CauD,CauTLDung) values(N'Dùng cáng khiêng thương binh bị thương ở bụng phải:', N'A: Đặt thương binh nằm sấp, kê đệm dưới bụng, giảm tránh các phủ tạng lòi ra ngoài.', N'B. Đặt thương binh nằm ngửa, kê đệm dưới chân làm cho chân hơi co lên, để giảm áp lực trong ổ bụng, giảm tránh các phủ tạng lòi ra ngoài.', N'C. Đặt nằm nghiêng và cột chặt xuống cáng tránh xê dịch, chân duỗi thẳng để giảm áp lực trong ổ bụng, giảm tránh các phủ tạng lòi ra ngoài.', N'D. Đặt thương binh nằm ngửa, chân duỗi thẳng, kê cao đầu chống khó thở.', 'b');
insert into CauHoi(NoiDung,CauA,CauB,CauC,CauD,CauTLDung) values(N'Dùng cáng khiêng thương binh bị thương ở vùng ngực phải:', N'A. Đặt thương binh trong tư thế nửa nằm, nửa ngồi nhằm giúp cho thương binh dễ thở.', N'B. Đặt thương binh trong tư thế nằm ngửa, chân hơi co lại để tránh các phủ tạng lòi ra ngoài.', N'C. Đặt thương binh trong tư thế nằm sấp, dùng tấm nệm kê dưới ngực.', N'D. Đặt thương binh trong tư thế nằm nghiêng, dùng băng quấn chặt hạn chế mất máu.', 'a');
insert into CauHoi(NoiDung,CauA,CauB,CauC,CauD,CauTLDung) values(N'Vết thương phần mềm:', N'A. Là loại vết thương có tổn thương da, gân, cơ, trong đó cơ là chủ yếu.', N'B. Là loại vết thương có tổn thương da, cơ, xương kèm theo đứt mạch máu.', N'C. Là loại vết thương không rách da, không chảy máu ra bên ngoài, còn gọi là chấn thương.', N'D. Là loại vết thương không rách da, không chảy máu ra bên ngoài, có thể tổn thương các phủ tạng trong bụng, ngực.', 'a');
insert into CauHoi(NoiDung,CauA,CauB,CauC,CauD,CauTLDung) values(N'Cách cấp cứu đầu tiên các vết thương phần mềm:', N'A. Băng vết thương, đưa thương binh về nơi an toàn, chờ dịp tổ chức vận chuyển về cơ sở điều trị.', N'B. Đưa thương binh về nơi an toàn, băng vết thương, chờ dịp tổ chức vận chuyển về cơ sở điều trị.', N'C. Nhanh chóng vận chuyển thương binh về cơ sở điều trị.', N'D. Cầm máu, chống choáng, hô hấp nhân tạo, băng vết thương, sau đó đưa về cơ sở điều trị.', 'a');
insert into CauHoi(NoiDung,CauA,CauB,CauC,CauD,CauTLDung) values(N'Băng vết thương phần mềm nhằm:', N'A. Nhằm bảo vệ vết thương, các mô không bị dập nát, hoại tử thêm, ngăn chặn chất độc theo đất cát xâm nhập, hạn chế mất máu.', N'B. Bảo vệ vết thương không bị ô nhiễm thêm, cầm máu tại vết thương, hạn chế được các biến chứng xấu.', N'C. Nhằm che kín vết thương không bị ô nhiễm thêm, chống choáng, chống khó thở, ngăn chặn các mầm bệnh, chất nhiễm xạ, phóng xạ xâm nhập.', N'D. Cả A và B đều đúng.', 'b');
insert into CauHoi(NoiDung,CauA,CauB,CauC,CauD,CauTLDung) values(N'Biến chứng của vết thương mạch máu:', N'A. Thường bị ngất xỉu do quá đau đớn, hôn mê, co giật, ói mửa.', N'B. Thường bị ngất xỉu do choáng, đau đớn, mất máu nhiều dễ dẫn đến hôn mê hoặc tử vong.', N'C. Choáng do mất máu nhiều dễ dẫn đến tử vong, vết thương bị ô nhiễm, chảy máu lần thứ hai.', N'D. Choáng do đau đớn và mất máu nhiều dễ dẫn đến tử vong, hôn mê, khó thở, ho.', 'c');
insert into CauHoi(NoiDung,CauA,CauB,CauC,CauD,CauTLDung) values(N'Yêu cầu cầm máu tạm thời là:', N'A. Khẩn trương, nhanh chóng, tiến hành đặt ga rô để cầm máu.', N'B. Khẩn trương, nhanh chóng vận chuyển thương binh ra khỏi nơi nguy hiểm, tổ chức đưa về tuyến sau điều trị.', N'C. Khẩn trương, nhanh chóng kiểm tra các vết thương khác kèm theo để xử trí, cầm máu tạm thời.', N'D. Khẩn trương, nhanh chóng đúng chỉ định theo yêu cầu của vết thương.', 'd');
insert into CauHoi(NoiDung,CauA,CauB,CauC,CauD,CauTLDung) values(N'Biện pháp cầm máu tạm thời phải:', N'A. Khẩn trương, nhanh chóng tiến hành đặt ga rô để cầm máu.', N'B. Tùy theo tính chất chảy máu để có biện pháp cho phù hợp, không làm bừa, làm ẩu, không được đặt ga rô tùy tiện.', N'C. Tùy theo tính chất chảy máu để có biện pháp cho phù hợp, không làm bừa, làm ẩu, chỉ khi các động mạch, tĩnh mạch chủ bị rách, đứt khỏi mới tiến hành ga rô.', N'D. Khẩn trương, nhanh chóng, tùy theo tính chất chảy máu để có biện pháp cho phù hợp, trường hợp nhiễm độc, nhiễm xạ mới tiến hành đặt ga rô.', 'b');
insert into CauHoi(NoiDung,CauA,CauB,CauC,CauD,CauTLDung) values(N'Biến chứng của vết thương gãy xương:', N'A. Thường bị ngất xỉu do quá đau đớn, hôn mê sâu, co giật, ói mửa.', N'B. Choáng do đau đớn và mất máu, nhiễm khuẩn nặng.', N'C. Choáng do mất máu nhiều dễ dẫn đến tử vong, bị ô nhiễm, chảy máu lần thứ hai.', N'D. Các mô dập nát và hoại tử, dị vật bám nhiều, nhiễm khuẩn nặng, ngất xỉu do quá đau đớn.', 'b');
insert into CauHoi(NoiDung,CauA,CauB,CauC,CauD,CauTLDung) values(N'Thứ tự các bước cấp cứu ban đầu vết thương gãy xương:', N'A. Cầm máu tạm thời (nếu kèm theo đứt mạch máu), cố định tạm thời gãy xương, băng (đối với vết thương hở), đưa về nơi an toàn.', N'B. Băng (đối với vết thương hở), cầm máu tạm thời (nếu kèm theo đứt mạch máu), cố định tạm thời gãy xương, đưa về nơi an toàn.', N'C. Đưa về nơi an toàn, cầm máu tạm thời (nếu kèm theo đứt mạch máu), băng (đối với vết thương hở), cố định tạm thời gãy xương.', N'D. Cầm máu tạm thời (nếu kèm theo đứt mạch máu), băng (đối với vết thương hở), cố định tạm thời gãy xương, đưa về nơi an toàn.', 'd');
insert into CauHoi(NoiDung,CauA,CauB,CauC,CauD,CauTLDung) values(N'Bỏng nặng là chiếm bao nhiêu % diện tích trên cơ thể trở lên:', N'A. 5%.', N'B. 10%.', N'C. 15%.', N'D. 20%.', 'b');
insert into CauHoi(NoiDung,CauA,CauB,CauC,CauD,CauTLDung) values(N'Cách cấp cứu bỏng ngoài hỏa tuyến:', N'A. Tìm cách dập tắt lửa, băng vết bỏng hoặc dùng vải sạch để phủ lại, cho uống thuốc giảm đau, uống nước muối và nabica, ủ ấm và nhẹ nhàng vận chuyển về tuyến sau.', N'B. Tìm cách dập tắt lửa, không được băng, dùng vải sạch để phủ lại, cho uống thuốc giảm đau, chích ngừa bệnh, ủ ấm và nhẹ nhàng vận chuyển về tuyến sau.', N'C. Tìm cách dập tắt lửa, nhặt bỏ các dị vật bám vào, dùng vải sạch để phủ lại, chích ngừa, uống nước muối và nabica, ủ ấm và nhẹ nhàng vận chuyển về tuyến sau.', N'D. Cả A, B, C đều đúng.', 'a');
insert into CauHoi(NoiDung,CauA,CauB,CauC,CauD,CauTLDung) values(N'Triệu chứng của hội chứng đè ép trong thời kỳ toàn phát:', N'A. Phần chi thể bị đè ép phù nề lan rộng, sưng to, biến dạng, đau đớn, không cử động được hoặc cử động khó khăn, da xám nhợt nhạt, lạnh.', N'B. Triệu chứng choáng xuất hiện, mạch nhanh, nhỏ, tụt huyết áp, nước tiểu giảm dần sau đó không tiểu tiện được, báo hiệu suy thận cấp, dễ dẫn tới tử vong.', N'C. Cảm giác như kiến bò xung quanh vùng bị đè ép, viêm tấy phù nề nhẹ, mạch nhỏ, ói mửa, khó thở.', N'D. Phần chi thể bị đè ép phù nề lan rộng, sưng to, biến dạng, mất cảm giác đau đớn, không cử động được hoặc cử động khó khăn, da xám nhợt nhạt, sốt cao.', 'a');
insert into CauHoi(NoiDung,CauA,CauB,CauC,CauD,CauTLDung) values(N'Khi các đoạn chi bị vùi lấp, đè ép chặt, ta phải xử trí:', N'A. Chống nóng lạnh cho nạn nhân, cho uống thuốc giảm đau, đặt dây ga rô sát trên chỗ bị đè ép, sau đó tháo gỡ phần chi thể bị đè ép.', N'B. Đặt dây ga rô sát trên chỗ bị đè ép với áp lực vừa phải cho máu lưu thông chậm lại khi chi được giải phóng.', N'C. Nhanh chóng tháo gỡ, đặt dây ga rô sát trên chỗ bị đè ép với áp lực vừa phải cho máu lưu thông chậm để chống choáng.', N'D. Đặt dây ga rô sát trên chỗ bị đè ép, băng cầm máu, cho uống thuốc giảm đau, hô hấp nhân tạo sau đó tháo gỡ phần chi bị đè ép.', 'b');
insert into CauHoi(NoiDung,CauA,CauB,CauC,CauD,CauTLDung) values(N'Cách xử trí vết thương thấu ngực mở:', N'A. Băng chặt, khâu hoặc nút kín, kê cao đầu, lau sạch đờm dãi, nhanh chóng vận chuyển về nơi phẩu thuật.', N'B. Dùng chén hoặc gáo dừa úp lại, ga rô cầm máu, kê cao đầu, lau sạch đờm dãi, nhanh vận chuyển về nơi phẩu thuật.', N'C. Cầm máu, băng chặt, cố định xương sườn, vận chuyển về tuyến sau điều trị.', N'D. Khẩn trương, nhanh chóng kiểm tra các vết thương khác kèm theo để xử trí, nhanh chóng vận chuyển về nơi phẩu thuật.', 'a');
insert into CauHoi(NoiDung,CauA,CauB,CauC,CauD,CauTLDung) values(N'Nguyên tắc chung khi cấp cứu đầu tiên vết thương sọ não, cột sống:', N'A. Chống choáng, lau sạch đờm dãi, chống khó thở, băng cầm máu, nhanh chóng dùng võng hoặc cõng dìu thương binh về tuyến sau.', N'B. Băng bó cầm máu đúng kỹ thuật; chống choáng, chống khó thở; nhanh chóng dùng cáng cứng nhẹ nhàng vận chuyển về tuyến sau.', N'C. Là vết thương nguy hiểm nên ta không được phép sơ cứu mà phải nhanh chóng tìm mọi biện pháp vận chuyển về tuyến sau để kịp thời cứu chữa.', N'D. Chống nóng lạnh, chống choáng, lau sạch đờm dãi chống khó thở, nhanh chóng dùng võng hoặc cáng cứng nhẹ nhàng vận chuyển về tuyến sau.', 'b');
insert into CauHoi(NoiDung,CauA,CauB,CauC,CauD,CauTLDung) values(N'Khi băng vai, nách, cẳng chân, bàn tay ta thường áp dụng:', N'A. Băng kiểu vòng xoắn.', N'B. Băng kiểu số 8.', N'C. Băng kiểu chữ thập.', N'D. Băng kiểu đặc biệt.', 'b');
insert into CauHoi(NoiDung,CauA,CauB,CauC,CauD,CauTLDung) values(N'Tác dụng của súng tiểu liên AK:', N'A. Súng tiểu liên AK trang bị cho một người sử dụng dùng hỏa lực, lưỡi lê, báng súng để tiêu diệt sinh lực địch; súng cấu tạo gọn nhẹ, bắn được cả liên thanh và phát một.', N'B. Súng tiểu liên AK là vũ khí tự động có hỏa lực mạnh của tiểu đội bộ binh trang bị cho một người sử dụng, dùng hỏa lực để tiêu diệt sinh lực địch, phá hủy phương tiện chiến tranh; súng chỉ bắn được liên thanh.', N'C. Súng tiểu liên AK là vũ khí có uy lực mạnh của phân đội do một người sử dụng để tiêu diệt các mục tiêu bằng sắt thép, sinh lực địch ẩn nấp trong công sự và các vật kiến trúc không kiên cố.', N'D. Súng tiểu liên AK trang bị cho một người sử dụng dùng hỏa lực, lưỡi lê, báng súng để tiêu diệt sinh lực địch; súng cấu tạo gọn nhẹ, chỉ bắn được phát một.', 'a');
insert into CauHoi(NoiDung,CauA,CauB,CauC,CauD,CauTLDung) values(N'Súng tiểu liên AK sử dụng đạn:', N'A. Sử dụng đạn kiểu 1943 do Trung Quốc và kiểu 1956 do Liên Xô sản xuất. Có các loại đầu đạn: đầu đạn thường, vạch đường, xuyên thép và đầu đạn cháy.', N'B. Sử dụng đạn kiểu 1953 do Liên Xô và kiểu 1956 do Trung Quốc sản xuất. Có các loại đầu đạn: đầu đạn thường, vạch đường, đầu đạn diệt sinh lực địch và đầu đạn phá hủy phương tiện chiến tranh.', N'C. Sử dụng đạn kiểu 1943 do Liên Xô và kiểu 1956 do Trung Quốc sản xuất. Có các loại đầu đạn: đầu đạn thường, vạch đường, xuyên cháy và đầu đạn cháy.', N'D. Sử dụng đạn kiểu 1953 do Liên Xô và kiểu 1966 do Trung Quốc sản xuất. Có các loại đầu đạn: đầu đạn thường, vạch đường, xuyên thép và đầu đạn bằng thép.', 'c');
