insert into CauHoi(NoiDung,CauA,CauB,CauC,CauD,CauTLDung) values(N'Khái niệm thuốc nổ:', N'A. Thuốc nổ là một hợp chất hoặc một hỗn hợp hóa học, khi bị tác động như nhiệt, hóa, cơ, thì có phản ứng nổ, sinh nhiệt cao, lượng khí lớn tạo thành áp lực mạnh phá hủy các vật thể xung quanh.', N'B. Thuốc nổ là một hợp chất hữu cơ hoặc một hỗn hợp hóa học, dùng để tiêu diệt sinh lực địch, phá hủy phương tiện chiến tranh, công sự, vật cản của địch, tăng tốc độ phá đất đá, làm công sự, khai thác gỗ.', N'C. Thuốc nổ là một loại vũ khí nổ thông thường, tinh thể trắng hoặc màu tro, độc, khi bị tác động như nhiệt, điện, hóa, cơ, thì có phản ứng nổ, sinh nhiệt cao, áp suất lớn, tạo thành sóng xung kích phá hủy các vật thể xung quanh.', N'D. Thuốc nổ là một hợp chất hoặc một hỗn hợp hóa học, khi bị tác động như nhiệt, hóa, cơ, thì có phản ứng nổ, tạo thành sóng xung kích, bức xạ quang, bức xạ xuyên và chất phóng xạ, có khả năng phá hủy các vật thể xung quanh.', 'a');
insert into CauHoi(NoiDung,CauA,CauB,CauC,CauD,CauTLDung) values(N'Tác dụng chung của thuốc nổ:', N'A. Dùng để cấu trúc các loại lượng nổ theo hình dạng khác nhau phù hợp với đặc điểm chỗ đặt khi phá vật thể, dùng làm lượng nổ lõm.', N'B. Dùng để tiêu diệt sinh lực địch, phá hủy phương tiện chiến tranh, công sự, vật cản của địch, tăng tốc độ phá đất đá, làm công sự, khai thác gỗ.', N'C. Dùng nhồi trong bom đạn, mìn, trộn với thuốc nổ mạnh làm dây nổ, ép thành từng bánh 75g, 200g, 400g để cấu trúc các loại lượng nổ.', N'D. Dùng năng lượng nhiệt, điện, hóa, cơ để gây nổ, khi nổ sinh nhiệt cao, lượng khí lớn tạo thành áp lực mạnh phá hủy các vật thể xung quanh.', 'b');
insert into CauHoi(NoiDung,CauA,CauB,CauC,CauD,CauTLDung) values(N'Đặc điểm nhận dạng của thuốc gây nổ Fulminat thủy ngân:', N'A. Tinh thể trắng, hạt nhỏ, khó tan trong nước.', N'B. Gồm 80% hexoghen + 20% chất dính kết, màu trắng đục, dẻo, mùi hắc, vị nhạt.', N'C. Tinh thể trắng hoặc màu tro, độc, khó tan trong nước lạnh, nhưng tan trong nước sôi.', N'D. Tinh thể cứng, màu vàng nhạt, tiếp xúc với ánh sáng ngả màu vàng nâu, vị đắng độc, khi đốt lửa đỏ, khói đen mùi nhựa thông.', 'c');
insert into CauHoi(NoiDung,CauA,CauB,CauC,CauD,CauTLDung) values(N'Cảm ứng nhiệt của thuốc gây nổ Fulminat thủy ngân:', N'A. Đốt khó cháy, nhiệt độ nóng chảy ở 79 đến 810C, cháy 3000C, nổ 3500C, nếu tăng nhiệt độ lên đột ngột 3000C nổ. Khi cháy lửa màu nâu, khói đen, độc.', N'B. Đốt khó cháy, tự cháy và nổ ở nhiệt độ 3100C.', N'C. Rất dễ bắt lửa, khi bắt lửa nổ ngay; ở nhiệt độ 160 đến 1700C tự nổ.', N'D. Đốt khó cháy, 1900C cháy, 2010C nổ, bắt lửa nhanh, cháy không có khói, khi cháy tập trung 50kg có thể nổ.', 'c');
insert into CauHoi(NoiDung,CauA,CauB,CauC,CauD,CauTLDung) values(N'Công dụng của thuốc gây nổ Fulminat thủy ngân:', N'A. Thường gói thành từng thỏi dài, mỗi thỏi 100g đến 200g dùng trong phá đất, đào đường hầm, khai thác mỏ.', N'B. Dùng để cấu trúc các loại lượng nổ theo hình dạng khác nhau phù hợp với đặc điểm chỗ đặt khi phá vật thể, dùng làm lượng nổ lõm.', N'C. Nhồi trong bom đạn, mìn, trộn với thuốc nổ mạnh làm dây nổ, ép thành từng bánh 75g, 200g, 400g để cấu trúc các loại lượng nổ.', N'D. Nhồi trong kíp nổ, hạt lửa, đầu nổ của các loại đạn, bom, mìn.', 'd');
insert into CauHoi(NoiDung,CauA,CauB,CauC,CauD,CauTLDung) values(N'Đặc điểm nhận dạng của thuốc gây nổ Azotua chì:', N'A. Tinh thể trắng, hạt nhỏ, khó tan trong nước.', N'B. Gồm 80% hexoghen + 20% chất dính kết, màu trắng đục, dẻo, mùi hắc, vị nhạt.', N'C. Tinh thể trắng hoặc màu tro, độc, khó tan trong nước lạnh, nhưng tan trong nước sôi.', N'D. Tinh thể cứng, màu vàng nhạt, tiếp xúc với ánh sáng ngả màu vàng nâu, vị đắng độc, khi đốt lửa đỏ, khói đen mùi nhựa thông.', 'a');
