insert into CauHoi(NoiDung,CauA,CauB,CauC,CauD,CauTLDung) values(N'Đặc điểm nhận dạng của thuốc nổ vừa TNT (tri nitro toluen):', N'A. Màu trắng đục, dẻo, mùi hắc, vị nhạt, tỉ trọng 1,4.', N'B. Tinh thể cứng, màu vàng nhạt, tiếp xúc với ánh sáng ngả màu vàng nâu, vị đắng độc, khi đốt lửa đỏ, khói đen mùi nhựa thông.', N'C. Tinh thể trắng hoặc màu tro, độc, khó tan trong nước lạnh, nhưng tan trong nước sôi.', N'D. Tinh thể trắng, hạt nhỏ, khó tan trong nước.', 'b');
