insert into CauHoi(NoiDung,CauA,CauB,CauC,CauD,CauTLDung) values(N'Nội dung làm công tác chuẩn bị chiến đấu của từng người trong chiến đấu tiến công gồm:', N'A. Xác định vị trí phòng ngự, cách đánh, bố trí vũ khí trang bị, chuẩn bị đầy đủ lương thực, thực phẩm.', N'B. Xác định tư tưởng, ý chí quyết tâm chiến đấu, nhận bổ sung vũ khí, trang bị, thuốc quân y, gói buộc lượng nổ.', N'C. Xác định tư tưởng, cách đánh, bố trí vũ khí, làm công sự, vật cản, đường cơ động, chuẩn bị đầy đủ vật chất đảm bảo cho chiến đấu.', N'D. Xác định tư tưởng, ý chí quyết tâm chiến đấu, cách đánh, bố trí vũ khí, làm công sự, vật cản, chuẩn bị vật chất bảo đảm cho chiến đấu.', 'b');
insert into CauHoi(NoiDung,CauA,CauB,CauC,CauD,CauTLDung) values(N'Trong chiến đấu tiến công, trước khi vận động đến gần địch, chiến sĩ phải:', N'A. Quan sát tình hình địch, tình hình ta, xem xét địa hình, thời tiết, vận động theo đường nào, đến đâu, thời cơ và động tác vận động trong từng đoạn, vị trí tạm dừng và cách nghi binh lừa địch.', N'B. Quan sát địch, triệt để lợi dụng địa hình, thời tiết, ánh sáng, tiếng động, để vận dụng các tư thế động tác cho phù hợp, bảo đảm vận động nhanh, bí mật, an toàn, đúng thời gian quy định.', N'C. Quan sát địch, thời tiết, địa hình, ánh sáng, luôn giữ đúng đường, đúng hướng, tìm mọi cách tiến đến gần mục tiêu.', N'D. Nhanh chóng chuẩn bị súng đạn, lựu đạn, pháo tay, quan sát nắm tình hình địch và hành động của đồng đội, nắm vững thời cơ tiến công tiêu diệt địch.', 'a');
insert into CauHoi(NoiDung,CauA,CauB,CauC,CauD,CauTLDung) values(N'Trong chiến đấu tiến công, khi vận động đến gần mục tiêu, chiến sĩ phải:', N'A. Phải quan sát địch, ta, địa hình, thời tiết, vận động theo đường nào, đến đâu, thời cơ và động tác vận động, vị trí tạm dừng, cách nghi binh lừa địch.', N'B. Phải quan sát địch, triệt để lợi dụng địa hình, thời tiết, ánh sáng, tiếng động, để vận dụng các tư thế động tác cho phù hợp, bảo đảm vận động nhanh, bí mật, an toàn, đúng thời gian quy định.', N'C. Quan sát địch, thời tiết, địa hình, ánh sáng, luôn giữ đúng đường, đúng hướng, nắm vững thời cơ tiến công tiêu diệt địch.', N'D. Phải nhanh chóng chuẩn bị súng đạn, lựu đạn, pháo tay, quan sát nắm tình hình địch, hành động của đồng đội, nắm vững thời cơ để xung phong tiêu diệt địch, chiếm mục tiêu.', 'd');
insert into CauHoi(NoiDung,CauA,CauB,CauC,CauD,CauTLDung) values(N'Trước khi đánh chiếm mục tiêu (ụ súng hoặc lô cốt), người chiến sĩ phải:', N'A. Phải quan sát nắm chắc địch, địa hình xung quanh, đường tiếp cận, hình thù, tính chất mục tiêu, nhanh chóng bí mật vận động đến bên sườn phía sau, dùng lựu đạn ném vào bên trong tiêu diệt địch.', N'B. Phải quan sát nắm chắc địch, địa hình xung quanh, đường tiếp cận, hình thù, tính chất mục tiêu, đặc điểm hoạt động, chỗ sơ hở yếu điểm như bên sườn, phía sau, đồng thời căn cứ vào vũkhí hiện có để xác định cách đánh cho phù hợp.', N'C. Triệt để tận dụng kết quả hỏa lực của cấp trên, bí mật vận động đến bên sườn phía sau, dùng lựu đạn ném vào bên trong tiêu diệt địch.', N'D. Quan sát địch, thời tiết, địa hình, ánh sáng, luôn giữ đúng đường, đúng hướng, nắm vững thời cơ tiến công tiêu diệt địch.', 'b');
insert into CauHoi(NoiDung,CauA,CauB,CauC,CauD,CauTLDung) values(N'Một trong những nội dung chiến sĩ phải nhớ kỹ khi nhận nhiệm vụ chiến đấu phòng ngự là:', N'A. Phương hướng, vật chuẩn, đặc điểm địa hình nơi phòng ngự.', N'B. Tình hình địch.', N'C. Thời gian hoàn thành công tác chuẩn bị, thời gian sẵn sàng đánh địch.', N'D. Cả 3 phương án trên.', 'd');
insert into CauHoi(NoiDung,CauA,CauB,CauC,CauD,CauTLDung) values(N'Trong chiến đấu phòng ngự, sau khi hiểu rõ nhiệm vụ, thứ tự các công việc người chiến sĩ phải làm là:', N'A. Xác định vị trí phòng ngự, xác định cách đánh, bố trí vũ khí, làm công sự và vật cản.', N'B. Xác định vị trí phòng ngự, làm công sự, bố trí vũ khí, xác định cách đánh.', N'C. Làm công sự, bố trí vũ khí, xác định cách đánh, bố trí vật cản.', N'D. Làm đường cơ động, đào hố bắn, triển khai súng, lựu đạn sẵn sàng đánh địch tập kích phía trước, bên sườn, phía sau.', 'a');
insert into CauHoi(NoiDung,CauA,CauB,CauC,CauD,CauTLDung) values(N'Trong chiến đấu phòng ngự, vị trí phòng ngự của từng người bao gồm:', N'A. Khu vực trên hướng bắn chính, hướng quan trọng, hướng bổ trợ.', N'B. Mục tiêu cần giữ và một số địa hình, địa vật xung quanh.', N'C. Hố bắn chính, hố bắn phụ, hào chiến đấu, hào giao thông.', N'D. Nơi tiện quan sát, tiện tiêu diệt địch.', 'b');
insert into CauHoi(NoiDung,CauA,CauB,CauC,CauD,CauTLDung) values(N'Cách đánh của chiến sĩ khi địch tiến công vào trận địa là:', N'A. Nhanh chóng dùng vũ khí ngăn chặn, tiêu diệt, đẩy lùi địch từ xa.', N'B. Lợi dụng địa hình địa vật, cơ động ra phía trước, hiệp đồng với đồng đội đánh chặn địch từ xa, giữ vững vị trí được giao.', N'C. Nắm vững thời cơ, chiếm lĩnh vị trí chiến đấu, chờ địch vào tầm bắn hiệu quả, theo lệnh người chỉ huy, hiệp đồng với đồng đội, tiêu diệt địch, giữ vững vị trí được giao.', N'D. Cả 3 phương án trên đều đúng.', 'c');
insert into CauHoi(NoiDung,CauA,CauB,CauC,CauD,CauTLDung) values(N'Trong chiến đấu phòng ngự, vũ khí bắn thẳng thường được bố trí:', N'A. Trên hướng bắn chính.', N'B. Phía trước mục tiêu cần giữ để tiêu diệt, ngăn chặn không cho địch đánh chiếm.', N'C. Tại hố bắn chính của người chiến sĩ.', N'D. Ở nhiều vị trí, nơi phát huy hết uy lực của vũ khí, hiểm hóc, bất ngờ, tiện cơ động đánh địch trong mọi tình huống.', 'd');
insert into CauHoi(NoiDung,CauA,CauB,CauC,CauD,CauTLDung) values(N'Quy định thứ tự làm công sự trong chiến đấu phòng ngự là:', N'A. Hố bắn chính, hố bắn phụ, hào chiến đấu, hào giao thông, các loại hầm ẩn nấp, nghỉ ngơi.', N'B. Hào giao thông, hào chiến đấu, các loại hố bắn, hầm ẩn nấp, nghỉ ngơi.', N'C. Hầm cất giấu vũ khí, lương thực thực phẩm, hố bắn chính, hố bắn phụ, hào chiến đấu, hào giao thông, các loại hầm ẩn nấp, nghỉ ngơi.', N'D. Hào chiến đấu, hào giao thông, các loại hố bắn, các loại hầm ẩn nấp, nghỉ ngơi.', 'a');
insert into CauHoi(NoiDung,CauA,CauB,CauC,CauD,CauTLDung) values(N'Để đảm bảo cho chiến đấu phòng ngự, vật chất từng người phải chuẩn bị:', N'A. Dụng cụ và vật liệu xây dựng công sự trận địa.', N'B. Các loại vũ khí, trang bị, phương tiện, vật chất.', N'C. Lương thực, thực phẩm, thuốc men.', N'D. Súng đạn, lựu đạn, mìn.', 'b');
insert into CauHoi(NoiDung,CauA,CauB,CauC,CauD,CauTLDung) values(N'Khi địch dùng hỏa lực bắn phá nhưng chưa tiến công bằng bộ binh hoặc bộ binh cơ giới, hành động của chiến sĩ là:', N'A. Báo cáo với cấp trên, thông báo với bạn, nhanh chóng về vị trí chiến đấu để sẵn sàng đánh địch.', N'B. Cơ động về vị trí chiến đấu, củng cố công sự trận địa, báo cáo với cấp trên.', N'C. Cơ động ra phía trước sẵn sàng đánh chặn địch ở phía trước trận địa.', N'D. Nếu không làm nhiệm vụ trực ban, phải triệt để lợi dụng công sự trận địa, địa hình, địa vật để ẩn nấp, tích cực chủ động quan sát nắm chắc tình hình.', 'd');
insert into CauHoi(NoiDung,CauA,CauB,CauC,CauD,CauTLDung) values(N'Khi địch tiến công bằng bộ binh hoặc bộ binh cơ giới, hành động của chiến sĩ là:', N'A. Cơ động ra phía trước, hiệp đồng với đồng đội đánh chặn địch ở phía trước trận địa.', N'B. Bố trí vật cản phía trước trận địa để ngăn chặn địch.', N'C. Nhanh chóng, bí mật chiếm vị trí chiến đấu, chờ địch đến gần, nắm vững thời cơ, bất ngờ nổ súng tiêu diệt địch.', N'D. Nhanh chóng chiếm vị trí chiến đấu, củng cố công sự trận địa, tăng cường quan sát, tiêu diệt địch trên hướng bắn chính.', 'c');
insert into CauHoi(NoiDung,CauA,CauB,CauC,CauD,CauTLDung) values(N'Khi bị địch chiếm một phần trận địa, hành động của chiến sĩ là:', N'A. Lùi về sau, báo cáo với cấp trên chi viện, phản kích lấy lại phần đất đã mất.', N'B. Vòng ra bên sườn, phía sau địch, dùng lựu đạn, thủ pháo, bắn găm, bắn gần, bất ngờ tiêu diệt địch, khôi phục lại trận địa, báo cáo với cấp trên.', N'C. Kiên quyết giữ vững phần trận địa còn lại, dùng vũ khí tiêu diệt, ngăn chặn không cho địch phát triển, báo cáo với cấp trên, phối hợp với đồng đội khôi phục lại trận địa.', N'D. Tùy theo từng tình huống chiến đấu có thể vận dụng một trong 3 phương án trên.', 'c');
insert into CauHoi(NoiDung,CauA,CauB,CauC,CauD,CauTLDung) values(N'Sau mỗi lần tiến công bị thất bại, địch thường rút lui về phía sau, hành động của chiến sĩ lúc này là:', N'A. Bắn truy kích cho đến khi địch chạy ra ngoài tầm bắn XA NHẤT của vũ khí, phán đoán thủ đoạn tiến công mới của địch, cứu chữa thương binh, củng cố công sự, sẵn sàng đánh địch tiến công tiếp theo.', N'B. Củng cố công sự, ngụy trang, phán đoán thủ đoạn tiến công mới của địch, báo cáo cấp trên.', N'C. Cơ động đuổi theo truy kích địch, cứu chữa thương binh, thu chiến lợi phẩm, bắt tù binh, hàng binh, báo cáo cấp trên.', N'D. Bắn truy kích cho đến khi địch chạy ra ngoài tầm bắn HIỆU QUẢ, phán đoán thủ đoạn tiến công mới của địch, cứu chữa thương binh, củng cố công sự, sẵn sàng đánh địch tiến công tiếp theo.', 'd');
